#!/usr/bin/python3# -*- coding: UTF-8 -*-from scapy.all import *import timedef handelPacket(packet):  # p捕获到的数据包    # packet.show()    if packet.haslayer('TCP'):        print('TCP Packet!')        '''        print(packet['TCP'].sport)        print(packet['TCP'].dport)        print(packet['TCP'].seq)        print(packet['TCP'].dataofs)        '''    if packet.haslayer('UDP'):        print('UDP Packet!')    if packet.haslayer('HTTP'):        print('HTTP Packet!')    if packet.haslayer('ARP'):        print('ARP Packet!')        '''        print(packet['ARP'].psrc)        print(packet['ARP'].pdst)        print(packet['ARP'].hwsrc)        print(packet['ARP'].hwdst)        '''# filter = "tcp"# sniff(filter=filter, prn=handelPacket, count=10)sniff(prn=handelPacket, count=10)