#!/usr/bin/python3# -*- coding: UTF-8 -*-from scapy.all import *import timeclass sniffer():    def print(self, a):        print(a)    def handelPacket(self, packet):  # p捕获到的数据包        # packet.show()        if packet.haslayer('TCP'):            print('TCP Packet!')            '''            print(packet['TCP'].sport)            print(packet['TCP'].dport)            print(packet['TCP'].seq)            print(packet['TCP'].dataofs)            '''        if packet.haslayer('UDP'):            print('UDP Packet!')        if packet.haslayer('IP'):            print('IP Packet!')        if packet.haslayer('HTTP'):            print('HTTP Packet!')        if packet.haslayer('ARP'):            print('ARP Packet!')            '''            print(packet['ARP'].psrc)            print(packet['ARP'].pdst)            print(packet['ARP'].hwsrc)            print(packet['ARP'].hwdst)            '''    def startSniff(self):        print('start sniff')        sniff(filter=None, prn=self.handelPacket, count=10)def main():    s = sniffer()    s.print('a')    s.startSniff()if __name__ == '__main__':    main()